script:
let content;

// Call the template's createLayout method to initialize the layout
const { defaultContainer } = playerApi.callTemplateScript('createLayout', {});

function generateContent() {
  // Set the content to be displayed inside the default container
  content = `{{content}}`;
  defaultContainer.innerHTML = content;
  playerApi.sendEvent('task_loaded', {});
}

function init() {
  // Handle event to get task details
  playerApi.receiveEvent('get_task_details', function() {
    const taskDetails = playerApi.getTaskDetails();
    const dynamicDetails = `The task displays custom content using the default template.`;
    playerApi.sendEvent('task_details', {
      staticInfo: taskDetails.description,
      dynamicDetails: dynamicDetails
    });
  });

  // Handle refresh event
  playerApi.receiveEvent('refresh', function() {
    generateContent();
  });
}

// Initialize and generate the content
init();
generateContent();
