script:
let num1, num2, answer, choices;
const NUM_CHOICES = 4;
let selectedChoice = null;

// Create the layout using the template's method
// Note: imagePlaceholder is not used in this task and will remain unchanged
const { questionElement, imagePlaceholder, choiceElements } = playerApi.callTemplateScript('createLayout', { choices: NUM_CHOICES, choicesData: [] });

// Function to generate the exercise
function generateExercise() {
  // Generate two random numbers
  num1 = (Math.random() * 98 + 1).toFixed(2);
  num2 = (Math.random() * 98 + 1).toFixed(2);

  // Calculate the correct answer by rounding the numbers and adding them
  const correct = Math.round(num1) + Math.round(num2);

  // Initialize the choices array with the correct answer
  choices = [correct];

  // Generate other choices ensuring they are unique
  for (let i = 0; i < NUM_CHOICES - 1; i++) {
    let delta;
    do {
      delta = Math.floor(Math.random() * 21) - 10;
    } while (choices.includes(correct + delta));
    choices.push(correct + delta);
  }

  // Sort the choices array
  choices.sort((a, b) => a - b);

  // Find the index of the correct answer
  answer = choices.indexOf(correct);

  // Set the question text using the variable
  questionElement.innerText = `{{text_question}} ${num1} + ${num2} = `;

  // Populate the choice elements with the choices
  choices.forEach((choice, index) => {
    choiceElements[index].innerText = choice;
  });

  // Send a task loaded event
  playerApi.sendEvent('task_loaded', {});
}

// Function to initialize event listeners
function init() {
  // Listener for getting task details
  playerApi.receiveEvent('get_task_details', function() {
    const taskDetails = playerApi.getTaskDetails();
    const dynamicDetails = `The exercise is ${num1} + ${num2}. The possible choices are: ${choices}. The correct answer is choice number ${answer + 1}, which is ${choices[answer]}.`;
    playerApi.sendEvent('task_details', {
      staticInfo: taskDetails.description,
      dynamicDetails: dynamicDetails
    });
  });

  // Listener for evaluating the task
  playerApi.receiveEvent('evaluate', function() {
    if (selectedChoice === null || selectedChoice === undefined) {
      playerApi.sendEvent('evaluationResult', { result: '{{text_no_choice}}' });
      return;
    }
    const isCorrect = selectedChoice === answer;
    playerApi.sendEvent('evaluationResult', {
      selectedId: selectedChoice,
      selectedContent: choices[selectedChoice],
      isCorrect
    });
  });

  // Listener for refreshing the task
  playerApi.receiveEvent('refresh', function() {
    choiceElements.forEach(choice => choice.classList.remove('selected'));
    selectedChoice = null;
    generateExercise();
  });

  // Listener for choice selection
  playerApi.receiveEvent('choice_selected', function(event) {
    const id = event.id;
    selectedChoice = id;
  });
}

// Initialize and generate the exercise
init();
generateExercise();
