script:
let selectedChoices = [];

const { questionElement, image_placeholder, choiceElements } = playerApi.callTemplateScript('createLayout', { choices: 5, choicesData: ['{{choice1}}', '{{choice2}}', '{{choice3}}', '{{choice4}}', '{{choice5}}'] });

questionElement.innerText = '{{text_question}}';
const img = document.createElement('img');
img.src = '/media/pizza_FO5M3iJ.png';
img.alt = 'Pizza divided into 6 equal pieces';
image_placeholder.appendChild(img);

function init() {
  playerApi.receiveEvent('get_task_details', function() {
    playerApi.sendEvent('task_details', {
      staticInfo: playerApi.getTaskDetails().description,
      dynamicDetails: 'Each piece of pizza represents one-sixth since the pizza is divided into 6 equal pieces. Correct answers are: [0, 1, 4].'
    });
  });
  playerApi.receiveEvent('evaluate', function() {
    const isCorrect = selectedChoices.length === 3 && selectedChoices.every(id => [0, 1, 4].includes(id));
    playerApi.sendEvent('evaluationResult', {
      userInput: selectedChoices,
      result: [0, 1, 4],
      isCorrect: isCorrect
    });
  });
  playerApi.receiveEvent('refresh', function() {
    choiceElements.forEach(choice => choice.querySelector('input').checked = false);
    selectedChoices = [];
  });
  playerApi.receiveEvent('choices_selected', function(event) {
    selectedChoices = event.map(choice => choice.id);
  });
}

init();
