styles:
.choice {
  padding: 10px;
  border: 1px solid #ccc;
  cursor: pointer;
  margin: 5px 0;
}

.choice.selected {
  background-color: #d3d3d3;
  border-color: #000;
}

.choice:hover {
  background-color: #f0f0f0;
}

scripts:
let selectedChoice = null;

function createLayout(details) {
  const questionElement = document.getElementById('question');
  const choicesContainer = document.getElementById('choices-container');
  choicesContainer.innerHTML = '';
  const choiceElements = [];

  for (let choiceIndex = 0; choiceIndex < details.choices; choiceIndex++) {
    const choiceElement = document.createElement('div');
    choiceElement.classList.add('choice');
    choiceElement.addEventListener('click', () => selectChoice(choiceElement, choiceIndex, details.choicesData[choiceIndex]));
    choicesContainer.appendChild(choiceElement);
    choiceElements.push(choiceElement);
  }

  return { questionElement, choiceElements };
}

function selectChoice(choiceElement, id, content) {
  if (selectedChoice) {
    selectedChoice.classList.remove('selected');
  }
  choiceElement.classList.add('selected');
  selectedChoice = choiceElement;
  playerApi.sendEvent('choice_selected', { id, content });
}

playerApi.callTemplateScript = (method, details) => {
  switch (method) {
    case 'createLayout':
      return createLayout(details);
    default:
      throw new Error(`Unknown method: ${method}`);
  }
};

playerApi.receiveEvent('refresh', function() {
  selectedChoice = null;
});
